for (const item of items) {
  const html = item.json.body.content;
  const $ = cheerio.load(html);

  let extractedDetails = {};

  // Determine the email type by checking the main alert title.
  const isBackupNotTakenAlert = $('b:contains("Backup Not Taken")').length > 0;
  const isAgentNotCheckingInAlert = $('b:contains("Agent Not Checking In")').length > 0;

  if (isBackupNotTakenAlert) {
    // --- PARSING LOGIC FOR "BACKUP NOT TAKEN" (EMAIL 1) ---

    const alertParagraphText = $('p[style="line-height:18.75pt"]').first().text();
    const agentDetailsParagraphHtml = $('p[style="line-height:18.75pt"]').eq(1).html();

    if (agentDetailsParagraphHtml) {
        const agentDetails = agentDetailsParagraphHtml.split('<br>').map(line => {
            const parts = line.split(':');
            return parts.length > 1 ? parts[1].trim() : '';
        });

        extractedDetails = {
            alertTitle: 'Backup Not Taken',
            alertType: 'Agent Alert',
            product: 'Endpoint Backup for PCs', // âœ… Original
            agentName: agentDetails[0],
            lastEventTimestamp: $('p[style="line-height:18.75pt"]').first().find('b').first().text().trim(),
            backupThreshold: $('p[style="line-height:18.75pt"]').first().find('b').last().text().trim(),
            agentIdentifierLabel: 'Agent Shortcode',
            agentIdentifier: agentDetails[1],
            organizationName: agentDetails[2],
            supportKbLink: $('a[href*="query=endpoint%20backup%20for%20pcs%20backup%20not%20taken"]').attr('href'),
            clientSettingsLink: $('a[href*="endpoint-backup/client"]').attr('href'),
            generalSupportLink: $('a[href="https://helpdesk.kaseya.com"]').first().attr('href'),
        };
    }

  } else if (isAgentNotCheckingInAlert) {
    // --- PARSING LOGIC FOR "AGENT NOT CHECKING IN" (EMAIL 2) ---

    const alertParagraph = $('p').filter((i, el) => $(el).text().includes('has stopped communicating')).first();
    const agentDetailsParagraphHtml = alertParagraph.next().next().html();

    if (agentDetailsParagraphHtml) {
