            const parts = line.split(':');
            return parts.length > 1 ? parts[1].trim() : '';
        });

        extractedDetails = {
            alertTitle: 'Agent Not Checking In',
            alertType: 'Agent Alert',
            product: 'Endpoint Backup', // âœ… Original (different from backup alerts)
            agentName: agentDetails[0],
            lastEventTimestamp: alertParagraph.find('b').first().text().trim(),
            backupThreshold: alertParagraph.find('b').last().text().trim(),
            agentIdentifierLabel: 'Agent ID', // âœ… Original
            agentIdentifier: agentDetails[1],
            organizationName: agentDetails[2],
            supportKbLink: $('a[href*="query=endpoint%20backup%20for%20pcs%20checkin%20failure"]').attr('href'),
            clientSettingsLink: $('a[href*="cloud-continuity/client"]').attr('href'),
            generalSupportLink: $('a:contains("Support Help Center")').first().attr('href'),
        };
    }
  }

  // Add the extracted data back to the item
  item.json.extractedDetails = extractedDetails;
}

return items;

