    }
  }];
}

console.log("Searching for device:", targetDeviceName);
console.log("RMM returned device:", targetDevice?.hostname);

let deviceStatus;
let deviceInfo = null;

// Check if we got a device and if it matches our search
if (!targetDevice) {
  deviceStatus = 'NOT_FOUND';
