    deviceClass: targetDevice.deviceClass,
    extIpAddress: targetDevice.extIpAddress
  };
  console.log(`âœ… Device found: ${targetDevice.hostname}, Online: ${targetDevice.online}, Status: ${deviceStatus}`);
}

return [{
  json: {
    ...inputData,
    deviceStatus,
    deviceInfo,
    statusCheckTimestamp: new Date().toISOString(),
    searchInfo: {
      searchedFor: targetDeviceName,
      foundDevice: targetDevice?.hostname || 'None',
      matchFound: targetDevice?.hostname === targetDeviceName
    }
  }
}];

const inputData = items[0].json;

// INPUT DATA
const backupFailureStartStr = inputData.extractedDetails?.lastEventTimestamp; // "December 20, 2024 15:23"
const deviceLastOnlineStr = inputData.deviceInfo?.lastSeen; // "2024-12-20T15:24:48.000Z"
const alertThreshold = inputData.extractedDetails?.backupThreshold; // "7 days"
const currentDate = new Date();

console.log("Backup failure started:", backupFailureStartStr);
console.log("Device last online:", deviceLastOnlineStr);
console.log("Alert threshold:", alertThreshold);

// Parse backup failure start date
let backupFailureStartDate;
try {
  backupFailureStartDate = new Date(backupFailureStartStr);

  // If standard parsing fails, try manual parsing for "December 20, 2024 15:23" format
  if (isNaN(backupFailureStartDate.getTime())) {
    const months = {
      'January': 0, 'February': 1, 'March': 2, 'April': 3,
      'May': 4, 'June': 5, 'July': 6, 'August': 7,
      'September': 8, 'October': 9, 'November': 10, 'December': 11
    };

    const parts = backupFailureStartStr.match(/(\w+)\s+(\d+),\s+(\d+)\s+(\d+):(\d+)/);
    if (parts) {
      const [, month, day, year, hour, minute] = parts;
      backupFailureStartDate = new Date(year, months[month], day, hour, minute);
    }
  }
} catch (error) {
  console.log('Error parsing backup failure date:', error.message);
  backupFailureStartDate = new Date(Date.now() - 24 * 60 * 60 * 1000); // fallback
}

// Parse device last online date
const deviceLastOnlineDate = deviceLastOnlineStr ? new Date(deviceLastOnlineStr) : new Date(0);

console.log("Parsed backup failure start:", backupFailureStartDate.toISOString());
console.log("Parsed device last online:", deviceLastOnlineDate.toISOString());

// STEP 1: Determine the overlap period
let actionableDays;
let actionableHours;
let actionablePeriodStart;
let actionablePeriodEnd;

if (deviceLastOnlineDate > backupFailureStartDate) {
  // Device was still online AFTER backup failure began
  actionablePeriodStart = backupFailureStartDate;
  actionablePeriodEnd = deviceLastOnlineDate;
  const actionablePeriodMs = deviceLastOnlineDate - backupFailureStartDate;
  actionableDays = actionablePeriodMs / (1000 * 60 * 60 * 24);
  actionableHours = actionablePeriodMs / (1000 * 60 * 60);

  console.log("Device was online during backup failure period");
  console.log(`Actionable period: ${actionableDays.toFixed(3)} days (${actionableHours.toFixed(1)} hours)`);
} else {
  // Device went offline BEFORE backup failure (or at same time)
  actionableDays = 0;
  actionableHours = 0;
  actionablePeriodStart = null;
  actionablePeriodEnd = null;

  console.log("Device was offline during backup failure period");
  console.log("Actionable period: 0 days");
}

// STEP 2: Assign priority based on actionable period (24 HOUR MINIMUM)
let priority;
let urgency;
let priorityId;
let urgencyId;
let priorityReason;

if (actionableDays === 0) {
  priority = "Medium";
  urgency = "Medium";
  priorityId = 2;     // âœ… Medium
  urgencyId = 2;      // âœ… Medium
  priorityReason = "Device was offline during backup failure period - no actionable time";
} else if (actionableDays < 1) {
  // Less than 24 hours online during failure
  priority = "Medium";
  urgency = "Medium";
  priorityId = 2;     // âœ… Medium
  urgencyId = 2;      // âœ… Medium
  priorityReason = `Backup failed for only ${actionableHours.toFixed(1)} hours while device was online - below 24 hour minimum actionable period`;
} else if (actionableDays <= 2) {
  // 1-2 days online during failure
  priority = "Medium";
  urgency = "Medium";
  priorityId = 2;     // Medium
  urgencyId = 2;      // Medium
  priorityReason = `Backup failed for ${actionableDays.toFixed(1)} days while device was online`;
} else if (actionableDays <= 3) {
  // 2-3 days online during failure
  priority = "High";
  urgency = "High";
  priorityId = 1;     // High
  urgencyId = 1;      // High
  priorityReason = `Backup failed for ${actionableDays.toFixed(1)} days while device was online`;
} else {
  // 3+ days online during failure
  priority = "Critical";
  urgency = "Critical";
  priorityId = 4;     // Critical
  urgencyId = 4;      // Critical
  priorityReason = `Backup failed for ${actionableDays.toFixed(1)} days while device was online`;
}

// Calculate additional metrics for context
const totalFailurePeriodMs = currentDate - backupFailureStartDate;
const totalFailureDays = totalFailurePeriodMs / (1000 * 60 * 60 * 24);

const deviceOfflinePeriodMs = currentDate - deviceLastOnlineDate;
const deviceOfflineDays = deviceOfflinePeriodMs / (1000 * 60 * 60 * 24);

// Human readable description
let actionableDescription;
if (actionableDays === 0) {
  actionableDescription = "No actionable period (device offline)";
} else if (actionableHours < 1) {
  actionableDescription = `${Math.round(actionableHours * 60)} minutes while online`;
} else if (actionableDays < 1) {
  actionableDescription = `${actionableHours.toFixed(1)} hours while online (below 24h minimum)`;
} else {
  actionableDescription = `${actionableDays.toFixed(1)} days while online`;
}

console.log(`Final Priority: ${priority} (ID: ${priorityId})`);
console.log(`Reason: ${priorityReason}`);

return [{
  json: {
    ...inputData,
    timeCalculation: {
      // Input timestamps
      backupFailureStart: backupFailureStartDate.toISOString(),
      deviceLastOnline: deviceLastOnlineDate.toISOString(),
      currentTime: currentDate.toISOString(),

      // Key calculations
      actionableDays: Math.round(actionableDays * 1000) / 1000,
      actionableHours: Math.round(actionableHours * 10) / 10,
      totalFailureDays: Math.round(totalFailureDays * 100) / 100,
      deviceOfflineDays: Math.round(deviceOfflineDays * 100) / 100,

      // Priority assignment
      priority,
      urgency,
      priorityId,        // âœ… Added numeric ID for Autotask
      urgencyId,         // âœ… Added numeric ID for Autotask
      priorityReason,
      actionableDescription,
      isCritical: priority === "Critical",

      // Analysis details
      analysis: {
        deviceWasOnlineDuringFailure: deviceLastOnlineDate > backupFailureStartDate,
        hasActionablePeriod: actionableDays > 0,
        meetsMinimumActionablePeriod: actionableDays >= 1,
        actionablePeriodStart: actionablePeriodStart?.toISOString() || null,
        actionablePeriodEnd: actionablePeriodEnd?.toISOString() || null
      }
    }
  }
}];

