const now = Date.now();
const days30 = 30 * 24 * 60 * 60 * 1000;

const siteMap = {};

for (const item of items) {
    const device = item.json;

    if (!device.lastSeen || (now - device.lastSeen > days30)) {
        continue;
    }

    if (!siteMap[device.siteName]) {
        siteMap[device.siteName] = {};
    }

    const lastSeenInDays = Math.round((now - device.lastSeen) / (1000 * 60 * 60 * 24));
    const lastAuditInDays = device.lastAuditDate
        ? Math.round((now - device.lastAuditDate) / (1000 * 60 * 60 * 24))
        : null;

    siteMap[device.siteName][device.hostname] = {
        description: device.description,
        lastSeenInDays,
        lastAuditInDays,
        lastLoggedInUser: device.lastLoggedInUser,
        antivirusProduct: device.antivirus?.antivirusProduct || null,
        portalUrl: device.portalUrl
    };
}

// Now turn siteMap into an array of objects for n8n
const output = Object.keys(siteMap).map(siteName => ({
    json: {
        siteName,
        devices: siteMap[siteName]
    }
}));

return output;

