// Get the input data - corrected order
const allItems = $input.all();

// Debug: Log the structure to understand what we're working with
console.log('Number of input items:', allItems.length);
console.log('First item keys:', Object.keys(allItems[0]));
if (allItems.length > 1) {
  console.log('Second item keys:', Object.keys(allItems[1]));
}

// Device data comes first, company data second
const siteDeviceData = allItems[0].json;  // Device data from first input
const companies = allItems[1].json;       // Company data from second input

// Additional debug
console.log('Device data type:', Array.isArray(siteDeviceData) ? 'array' : typeof siteDeviceData);
console.log('Companies type:', Array.isArray(companies) ? 'array' : typeof companies);

const results = [];

// Ensure we have arrays to work with
if (!Array.isArray(companies)) {
  throw new Error('Companies data is not an array. Got: ' + typeof companies);
}

if (!Array.isArray(siteDeviceData)) {
  throw new Error('Site device data is not an array. Got: ' + typeof siteDeviceData);
}

// Process each company
for (const company of companies) {
  console.log(`Processing company: ${company.companyName}`);

  // Find matching site data by company name
  const matchingSite = siteDeviceData.find(site => {
    // Try exact match first
    if (site.siteName === company.companyName) return true;

    // Try partial matches for cases like "Number 16 Periodontics" vs "Montrose Healthcare LTD t/a Number 16 Periodontics"
    const siteName = site.siteName.toLowerCase();
    const companyName = company.companyName.toLowerCase();

    return siteName.includes(companyName) || companyName.includes(siteName);
  });

  if (matchingSite && matchingSite.devices) {
    const devices = matchingSite.devices;
    const deviceEntries = Object.entries(devices);
    const deviceCount = deviceEntries.length;

    console.log(`Found ${deviceCount} devices for ${company.companyName}`);

    // Only create tickets for sites with devices
    if (deviceCount > 0) {
      // Build device details section
      let deviceDetails = '';

      for (const [hostname, device] of deviceEntries) {
        deviceDetails += `Hostname: ${hostname}
Description: ${device.description}
Last Seen: ${device.lastSeenInDays} days ago
Last Audit: ${device.lastAuditInDays} days ago
Last Logged In User: ${device.lastLoggedInUser}
Operating System: N/A
External IP: N/A
Antivirus Installed: ${device.antivirusProduct}
Expected: Datto AV
RMM Link: ${device.portalUrl}

---
`;
      }

      // Build the complete description
      const description = `WRONG ANTIVIRUS INSTALLED: ANTIVIRUS COMPLIANCE REPORT

ORGANISATION: ${company.companyName}

---
STATUS SUMMARY
---
Total devices in this site not compliant: ${deviceCount}

---
DEVICE DETAILS
---
${deviceDetails}`;

      results.push({
        json: {
          companyId: company.id,
          companyName: company.companyName,
          siteName: matchingSite.siteName,
          deviceCount: deviceCount,
          description: description,
          // Add other fields you might need for Autotask ticket creation
          title: `Antivirus Compliance Issue - ${company.companyName}`,
          priority: 'Medium',
          status: 'New'
        }
      });
    }
  } else {
    // Log companies without matching device data
    console.log(`No device data found for company: ${company.companyName}`);
  }
}

console.log(`Created ${results.length} tickets for companies with non-compliant devices`);
return results;
